import React , {useState , useContext} from 'react'

const BookForm = () => {

    const [formValue , setFormValue] = useState({
        title : '',
        author: ''
    })

    useEffect(() => {
        console.log(title);
        
    }, [title])

    const handleChange = (e) => {
        const v1 = e.target.tp;
        setFormValue({v1:e.target.value})
    }

    return (
        <form>
            <input onChange={e => handleChange(e)} tp="title" type="text" />
            <input tp="author" type="text" />
        </form>
    )
}

export default BookForm
