export const listReducer = (state , action) => {
  switch (action.type) {
    case "ADD_TASK":
        console.log(state);
        console.log('payload - >>> ' , action.do);
        return [...state, {do:action.text , date:action.date, time:action.time}]
        //return [...state, action.payload]
        //return [...state , action.payload]
    default:
      return state;
  }
};
