import React, { useState, useReducer, useContext } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import TimePicker from "react-time-picker";
import { listReducer } from "../reducer";
import { ListContext } from "./To-do-list";
const Form = () => {
  const [value, setValue] = useState("");
  const handleChange = (e) => {
    setValue(e.target.value);
  };

  const dispatch = useContext(ListContext);

  return (
    <form className="form flex">
      <h1>To-Do List App </h1>
      <div className="row">
        <input
          onChange={(e) => handleChange(e)}
          value={value}
          className="border"
          type="text"
          placeholder="Enter the task"
        />
      </div>

      <div className="w-100 main-container">
        <div className="container-one">
          <TimePicker />
        </div>
        <div className="container-two">
          <DatePicker className="full-center date-picker-manual" />
        </div>
        <div className="container-three">
          <input
            onClick={(e) => {e.preventDefault() ; dispatch({type:'ADD_TASK' , payload:{do:value , date:"Osama", time:"Osama"}})}}
            value="Add"
            className="submit "
            type="submit"
          />
        </div>
      </div>
    </form>
  );
};

export default Form;
