export const listReducer = (state , action) => {
  switch (action.type) {
    case "ADD_TASK":
        console.log(state);
        console.log('payload - >>> ' , action.payload);
        return [...state, {date:action.date, time:action.time, text:action.text}]
        //return [...state, action.payload]
        //return [...state , action.payload]
    default:
      return state;
  }
};
