import React , {useState , useContext} from 'react'

const BookForm = () => {

    const [formValue , setFormValue] = useState({
        title : '',
        author: ''
    })

    React.useEffect(() => {
        console.log(formValue);
        
    }, [formValue.title])

    const handleChange = (e) => {
        const currentValue = e.target.value;
        console.log(currentValue);
        setFormValue({[formValue.title]:currentValue})
    }

    return (
        <form>
        <button onClick = {(e) =>  {e.preventDefault() ;setFormValue({...formValue, [formValue.title]:"dwe"})}}>
        click me </button>
            <input alt="title" onChange={e => handleChange(e)}  type="text" />
            <input alt="author" type="text" />
        </form>
    )
}

export default BookForm
