import Task from "./Task";
import { createContext } from "react";

export const ListContext = createContext();


const ListGrid = ({ tasks }) => {
  return (
    <ListContext.Provider value = {tasks}>
      <div>
        {tasks.map((task) => (
          <Task task={task} />
        ))}
        <button
          onClick={() => {
            console.log(tasks);
          }}
        >
          test
        </button>
      </div>
    </ListContext.Provider>
  );
};

export default ListGrid;
