export const listReducer = (state , action) => {
  switch (action.type) {
    case "ADD_TASK":
        console.log(state);
        console.log('payload - >>> ' , action.payload);
        const data = action.payload;
        return [...state, {data}]
        //return [...state , action.payload]
    default:
      return state;
  }
};
