import React, { useState, useContext } from "react";

const BookForm = () => {
  const [formValue, setFormValue] = useState({
    title: "",
    author: "",
  });

  React.useEffect(() => {
    console.log(formValue);
  }, [formValue]);

  const handleChange = (e) => {
    const currentValue = e.target.value;
    console.log(currentValue);
    setFormValue({ ...formValue, [e.target.alt]: currentValue });
  };

  return (
    <form onSubmit = {(e) => {e.preventDefault() ; setFormValue({ ...formValue, [formValue.title]: "dwe" })}}>
      <input alt="title" onChange={(e) => handleChange(e)} type="text" />
      <input alt="author" type="text" onChange={(e) => handleChange(e)} />
      <input type = "submit" value = "add book" />
    </form>
  );
};

export default BookForm;
