import React , {useState , useContext} from 'react'

const BookForm = () => {

    const [formValue , setFormValue] = useState({
        title : '',
        author: ''
    })

    React.useEffect(() => {
        console.log(formValue.title);
        
    }, [formValue.title])

    const handleChange = (e) => {
        const v1 = e.target.alt;
        setFormValue({v1:e.target.value})
    }

    return (
        <form>
        <button onClick = {() => console.log(formValue.title)}>click me </button>
            <input alt="title" onChange={e => handleChange(e)}  type="text" />
            <input alt="author" type="text" />
        </form>
    )
}

export default BookForm
