const initState = [
  { do: "goint to the gym", date: "20/10/2021", time: "10:20" },
  { do: "goint to the gym", date: "20/10/2021", time: "10:20" },
  { do: "goint to the gym", date: "20/10/2021", time: "10:20" },
];

export const listReducer = (state = initState, action) => {
  switch (action.type) {
    case "ADD_TASK":
      return [...state, action.payload];
    default:
      return state;
  }
};
