const BookForm = () => {

    const [formValue , setFormValue] = useState({
        title : '',
        author: ''
    })

    React.useEffect(() => {
        console.log(formValue.title);
        
    }, [formValue.title])

    const handleChange = (e) => {
        console.log(e.target.type);
    }

    return (
        <form>
            <input tp="title" onChange={e => handleChange(e)}  type="text" />
            <input tp="author" type="text" />
        </form>
    )
}

export default BookForm
