import { createContext } from "react";



const ListGrid = ({ tasks }) => {
  return (
      <div>
        {tasks.map((task) => (
          <Task task={task} />
        ))}
        <button
          onClick={() => {
            console.log(tasks);
          }}
        >
          test
        </button>
      </div>
  );
};

export default ListGrid;
