import React, { useState, useReducer, useContext } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import TimePicker from "react-time-picker";
import { listReducer } from "../reducer";
import { ListContext } from "./To-do-list";
const Form = () => {
  const [value, setValue] = useState({do:'' , time:'' , date:new Date});
  const [date ,setDate] = useState(new Date())

  const dispatch = useContext(ListContext);

  React.useEffect(() => {
    console.log(value);
  })
  return (
    <form className="form flex">
      <h1>To-Do List App </h1>
      <div className="row">
        <input
          value={value.do}
          className="border"
          type="text"
          placeholder="Enter the task"
          alt="do"
        />
      </div>

      <div className="w-100 main-container">
        <div className="container-one">
          <TimePicker alt="time" />
        </div>
        <div className="container-two">
          <DatePicker selected={date} onChange = {(date) => {setDate(date)}}  alt="date" className="full-center date-picker-manual" />
        </div>
        <div className="container-three">
          <input
            onClick={(e) => {e.preventDefault() ; dispatch({type:'ADD_TASK' , payload:{do:value.do , date:"Osama", time:"Osama"}})}}
            value="Add"
            className="submit "
            type="submit"
          />
        </div>
      </div>
    </form>
  );
};

export default Form;
